import { TestBed } from '@angular/core/testing';
import { IndexComponent } from './index.component';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { RouterTestingModule } from '@angular/router/testing';
import { ReactiveFormsModule } from '@angular/forms';
import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';
describe('AuthComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [RouterTestingModule, HttpClientTestingModule, ReactiveFormsModule],
            declarations: [IndexComponent],
            schemas: [CUSTOM_ELEMENTS_SCHEMA]
        })
            .compileComponents();
    });
    function updateForm(userEmail, userPassword) {
        component.logForm.controls['username'].setValue(userEmail);
        component.logForm.controls['password'].setValue(userPassword);
    }
    beforeEach(() => {
        fixture = TestBed.createComponent(IndexComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should log in a previously registered user', () => {
        updateForm("Bret", "Kulas Light");
        fixture.detectChanges();
        expect(component.logForm.valid).toBeTruthy();
    });
    it('should not log in an invalid user', () => {
        updateForm("David", "Kulas Light");
        component.onSubmit();
        expect(fixture.debugElement.nativeElement.querySelector('#username-error-msg').hidden);
    });
});
